"use strict";function localeDetect(){return(navigator.language||navigator.browserLanguage||navigator.systemLanguage||navigator.userLanguage).toLowerCase().substr(0,2)}function localeRedirect(){switch(localeDetect()){case"en":window.location.href="/en/";break;case"ru":window.location.href="/ru/";break;default:window.location.href="/ru/"}}document.addEventListener("DOMContentLoaded",(function(){localeRedirect()}));